let e=parseFloat(localStorage.getItem("fontSize"))||14;document.documentElement.style.fontSize=e+"px";var t=document.querySelector(".increase-font"),n=document.querySelector(".decrease-font");t.addEventListener("click",o);n.addEventListener("click",c);function o(){e<20&&(e+=2,document.documentElement.style.fontSize=e+"px",localStorage.setItem("fontSize",e))}function c(){e>14&&(e-=2,document.documentElement.style.fontSize=e+"px",localStorage.setItem("fontSize",e))}
